import React, { useState } from "react";
import ReactDOM from "react-dom";

const Anecdote = ({ anecdote, handleVoting }) => {
  const onClick = (index) => handleVoting(index);
  return (
    <>
      <p>
        {anecdote.text} <br />
        has {anecdote.votes} votes
      </p>
      <Button text={"vote"} handleClick={onClick} />
    </>
  );
};

const Button = (props) => {
  return <button onClick={props.handleClick}> {props.text} </button>;
};
const MostVoted = ({ votes, anecdotes }) => {
  let returnValue = votes[0];
  let i = 0;

  for (let index = 0; index <= votes.length; index++) {
    if (votes[index] > returnValue) {
      returnValue = votes[index];
      i = index;
    }
  }

  return (
    <div>
      <p>
        {anecdotes[i]} <br />
        has {returnValue} votes
      </p>
    </div>
  );
};

const App = (props) => {
  const [selected, setSelected] = useState(0);
  const [anecdotes, setAnecdotes] = useState(anecdoteObjectArray);

  const handleSelection = (index) => {
    setSelected(index);
  };
  const handleVoting = (index) => {
    const copy = { ...anecdotes };
    copy[index] += 1;
    setAnecdotes(copy);
  };
  const selectRandomAnecdote = () => {
    const randomIndex = Math.floor(Math.random() * anecdotes.length);
    setSelected(randomIndex);
  };

  const selectedAnecdote = anecdotes.find(
    (anecdote) => anecdote.id === selected
  );
  const mostVotedAnecdote = anecdotes.sort((a, b) => a.votes - b.votes)[0];

  console.log(anecdoteObjectArray);
  return (
    <div>
      <h2>Anecdote of the day</h2>
      <Anecdote anecdote={selectedAnecdote} handleVoting={handleVoting} />

      <Button text={"next anecdote"} handleClick={selectRandomAnecdote} />
      <h2>Anecdote with most votes</h2>
      <Anecdote anecdotes={mostVotedAnecdote} />
    </div>
  );
};

const anecdotes2 = [
  "If it hurts, do it more often",
  "Adding manpower to a late software project makes it later!",
  "The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.",
  "Any fool can write code that a computer can understand. Good programmers write code that humans can understand.",
  "Premature optimization is the root of all evil.",
  "Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.",
];

const anecdoteObjectArray = anecdotes2.map((anecdote, index) => ({
  id: index,
  text: anecdote,
  votes: 0,
}));

const adenctodeDbObjet = anecdotes2.reduce((acc, curr, idx) => {
  acc[idx] = curr;
  return acc;
}, {});

ReactDOM.render(
  <App anecdotes={anecdoteObjectArray} />,
  document.getElementById("root")
);
