import React, { useState } from 'react'
import ReactDOM from 'react-dom'




const Button = (props) => {


  const select = props.select
  return(

    <button onClick={props.handleClick}> {props.text} </button> 
  )
}
const MostVoted = (props) => {

  const array = props.votes
  let returnValue = array[0]
  let i = 0

  for (let index = 0; index <= array.length; index++) {
  if(array[index] > returnValue){
    returnValue = array[index]
    i = index
  }

  }



  return(
    <div>
      <p>props.anecdotes[i]</p>
      <p>has {returnValue} votes</p>
    </div>
  )
}

const App = (props) => {



  const points = Array(6).fill(0)



  const [selected, setSelected] = useState(0)
  const [votes, updateVotes] = useState(points)


  const handleClick = () => {
  setSelected(Math.floor((Math.random() * 6) + 0))
  }
  const handleClick2 = () => {
    const copy = [...votes]
    copy[selected] += 1  
    updateVotes(copy)

  } 



  console.log(selected)
  const value = selected


     console.log(votes)

  return (

    <div>
      <h2>Anecdote of the day</h2>
      <p>{props.anecdotes[selected]}</p>
      <p>has votes {votes[value]}</p>
      <Button text={"vote"} handleClick={handleClick2} />
      <Button text={"next anecdote"} handleClick={handleClick} />
      <h2>Anecdote with most votes</h2>

      </div>
  )    
}



const anecdotes = [
  'If it hurts, do it more often',
  'Adding manpower to a late software project makes it later!',
  'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
  'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
  'Premature optimization is the root of all evil.',
  'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
]


ReactDOM.render(
  <App anecdotes={anecdotes}/>,
  document.getElementById('root')
)
