import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import personService from "./services/persons2";

const Names = ({ names, deleteName }) => {
  return (
    <div>
      {names.name}
      {names.number}
      <button onClick={deleteName}>delete</button>
    </div>
  );
};

const App = () => {
  const [persons, setPersons] = useState([{ name: "jouni", number: "34" }]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    console.log("effect");
    personService.getAll().then((response) => {
      setPersons(response.data);
    });
  }, []);
  console.log("render", persons.length, "notes");

  const addPerson = (event) => {
    event.preventDefault();

    if (!persons.some((p) => p.name === newName)) {
      console.log("button clicked", event.target);

      const nameObject = {
        name: newName,
        number: newNumber,
      };

      personService.create(nameObject).then((response) => {
        setPersons(persons.concat(response.data));
        setNewName("");
        setNewNumber("");
      });
    } else {
      window.alert(`${newName} has already added to phonebook`);
    }
  };

  const handleNameChange = (event) => {
    console.log(event.target.value);
    setNewName(event.target.value);
  };
  const handleNumberChange = (event) => {
    console.log(event.target.value);
    setNewNumber(event.target.value);
  };

  const handleNameSearch = (event) => {
    setSearchInput(event.target.value);
  };
  const namesToShow = persons.filter((p) => p.name.includes(searchInput));

  const handleClick = (name) => {
    const id = persons
      .filter((person) => person.name.includes(name))
      .map((p) => p.id);
    axios.delete("http://localhost:3001/persons/" + id).then((response) => {
      console.log("person removed from the phonbook");
    });
  };
  return (
    <div>
      <h2>Phonebook</h2>
      <input value={searchInput} onChange={handleNameSearch} />
      <h2>Add a new</h2>
      <form onSubmit={addPerson}>
        <div>debug: {newName}</div>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Names</h2>

      {namesToShow.map((name, i) => (
        <Names key={i} names={name} deleteName={handleClick(name.name)} />
      ))}
    </div>
  );
};

export default App;
